// ProjectList.js
import React, { useState } from 'react';
import HiddenModals from './hiddenModals';

// import Vmarine from "../../../images/Vmarine.jpg";
// import aguaDeLuz from "../../../images/aguaDeLuz.png";
// import todo from "../../../images/todolist.png";

const ProjectList = () => {
    const [projects] = useState([
        {
            name: "Zahrawi Education System",
            // image: Vmarine,
            button: "Zahrawi Education System"
        },
        {
            name: "PingMeUp Chat App",
            // image: aguaDeLuz,
            button: "Chat App"
        },
        {
            name: "todolist",
            // image: todo,
            button: "todo"
        }
    ]);

    function showModal(name) {
        const modalBG = document.getElementById("gallery-card");
        const modal = document.getElementById(name);
        modalBG.style.display = "block";
        modal.style.display = "block";
    }

    function addbackground(image) {
        return {
            backgroundImage: `url(${image})`
        };
    }

    return (
        <div className="row my-5">
            {
                projects.map((project, index) => (
                    <div className={"testbg col-12 col-sm-12 col-md-4 wow fadeIn"} key={index} style={addbackground(project.image)}>
                        <div className="overlay">
                            <div className="text">
                                <h2>{project.name}</h2>
                            </div>
                            <div>
                                <button id={project.button + "-button"} className="project-button" onClick={() => showModal(project.name)}>
                                    Learn More
                                </button>
                            </div>
                        </div>
                    </div>
                ))
            }
            <HiddenModals projects={projects} />
        </div>
    );
}

export default ProjectList;
