import React from 'react';

const ServiceCard = ({ iconClass, title, description, imageUrl }) => {
  return (
    <div className="item">
      {imageUrl && <img src={imageUrl} alt={title} className="img-fluid mb-3 service-image" />}
      <span className={`icon ${iconClass}`}><i className={`fa ${iconClass}`}></i></span>
      <h6>{title}</h6>
      <p>{description}</p>
      <button className="btn btn-primary">Contact Us</button>
    </div>
  );
};

const Services = () => {
  const servicesData = [
    {
      iconClass: 'fa-laptop',
      title: 'Web App Development',
      description: 'Our Custom Web Development Services Include Both Front-End And Back-End Development. Whether It Is Enhancing An Existing App Or Architecting An Enterprise App, Our Developers Are Up For The Challenge.',
      imageUrl: '' // You can add a placeholder image or leave it blank if not applicable
    },
    {
      iconClass: 'fa-android',
      title: 'Mobile App Development',
      description: 'We Have Expertise In Creating Multi-Platform Mobile App Solutions For Both Android And IOS Devices. Using PhoneGap, Xamarin, And React Native, We Offer Custom Mobile App That Runs Smoothly On Multiple Platforms.',
      imageUrl: 'https://img.freepik.com/free-vector/app-development-banner_33099-1720.jpg'
    },
    {
      iconClass: 'fa-magic',
      title: 'Digital Marketing',
      description: 'The digital marketing services that we provide have their own set of charms. By taking our digital marketing services, our clients will be able to increase visibility and engage with their customers at the online platform.',
      imageUrl: 'https://p7.hiclipart.com/preview/954/642/575/digital-marketing-business-service-pay-per-click-market.jpg'
    },
    {
      iconClass: 'fa-paint-brush',
      title: 'Graphic Designing',
      description: 'Our Graphic Designing services ensure visually appealing and creative designs that capture attention and convey the right message.',
      imageUrl: 'https://example.com/graphic-design-image.jpg' // Replace with the actual URL
    },
    {
      iconClass: 'fa-search',
      title: 'SEO Services',
      description: 'Improve your online visibility and ranking on search engines with our SEO services. Drive organic traffic and increase your website\'s performance.',
      imageUrl: 'https://example.com/seo-services-image.jpg' // Replace with the actual URL
    },
    {
      iconClass: 'fa-database',
      title: 'CRM Softwares',
      description: 'Optimize customer relationship management with our powerful CRM software solutions. Streamline processes and enhance customer interactions.',
      imageUrl: 'https://example.com/crm-software-image.jpg' // Replace with the actual URL
    },
    {
      iconClass: 'fa-apple',
      title: 'iOS Apps',
      description: 'Crafting exceptional iOS applications tailored to meet the unique needs and preferences of Apple users.',
      imageUrl: 'https://example.com/ios-apps-image.jpg' // Replace with the actual URL
    },
    {
      iconClass: 'fa-android',
      title: 'Android Apps',
      description: 'Developing feature-rich Android applications to reach a wider audience on various Android devices.',
      imageUrl: 'https://example.com/android-apps-image.jpg' // Replace with the actual URL
    },
    {
      iconClass: 'fa-search',
      title: 'App Store Optimization',
      description: 'Enhance the visibility and discoverability of your mobile app on app stores with effective optimization strategies.',
      imageUrl: 'https://example.com/app-store-optimization-image.jpg' // Replace with the actual URL
    },
  ];

  return (
    <div className="pt-5 pb-5 services-container">
      <div className="container">
        <div className="row services-row">
          <div className="section-head col-sm-12" id="service">
            <h1>Our Services</h1>
            <p>
              We help you to build high-quality digital solutions and products as well as deliver a wide range of related professional services. We are providing world-class service to our clients.
            </p>
          </div>

          {servicesData.map((service, index) => (
            <ServiceCard
              key={index}
              iconClass={service.iconClass}
              title={service.title}
              description={service.description}
              imageUrl={service.imageUrl}
            />
          ))}

        </div>
      </div>
    </div>
  );
};

export default Services;
