import React, { useRef, useState } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { motion, AnimatePresence } from 'framer-motion';
import bg_slide1 from "../../images/img1.jpg";

import './carousel.css'; 

function PrevArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
    className={className}
    style={{
      ...style,
      display: "block",
      padding: "10px", // Adjust the padding to a reasonable size
      borderRadius: "50%", // Optional: Make the arrow circular
      cursor: "pointer", // Optional: Add a pointer cursor on hover
      left: "0px", // Position the arrow on the left side
      zIndex: 1, // Ensure the arrow is above other elements
    }}
    onClick={onClick}
  >

  </div>
  );
}
function NextArrow(props) {
  const { className, style, onClick } = props;



  return (
    <div
      className={className}
      style={{
        ...style,
        display: "block",
        padding: "50px", // Add some padding to make the arrow visible
        borderRadius: "100%", // Optional: Make the arrow circular
        cursor: "pointer", // Optional: Add a pointer cursor on hover
        zIndex: 1, 
      }}
      onClick={onClick}
    >

    </div>
  );
}

const SlideContent = ({ title, description, buttonText, imageSrc, index }) => {
  const isImageOnRight = index !== 0 && imageSrc; // Check if it's not the first slide and there's an image

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.5 }}
      animate={{ opacity: 1, scale: 1 }}
      exit={{ opacity: 0, scale: 0.5 }}
      transition={{ duration: 0.8, ease: 'easeOut' }}
      className="content"
    >
      <div className={`text-content${isImageOnRight ? ' left-align' : ''}`}>
        <motion.h1
          initial={{ rotateY: 180 }}
          animate={{ rotateY: 0 }}
          transition={{ duration: 1, ease: 'easeOut' }}
        >
          {title}
        </motion.h1>
        <motion.p
          initial={{ x: -50, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ duration: 0.8, ease: 'easeOut', delay: 1.5 }}
        >
          {description}
        </motion.p>
        <motion.button
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ duration: 0.8, ease: 'easeOut', delay: 2.5 }}
        >
          {buttonText}
        </motion.button>
      </div>
      {isImageOnRight && (
        <div className="image-content">
          <img src={imageSrc} alt={title} />
        </div>
      )}
    </motion.div>
  );
};

const Carousel = () => {
  const [areArrowsVisible, setAreArrowsVisible] = useState(false);
  const sliderRef = useRef(null);
  const [activeSlide, setActiveSlide] = useState(0);
  const [activeSlide2, setActiveSlide2] = useState(0);
  const handleMouseEnter = () => {
    setAreArrowsVisible(true);
  };

  const handleMouseLeave = () => {
    setAreArrowsVisible(false);
  };


  var settings = {
    dots: true,
    infinite: true,
    focusOnSelect: true,
    speed: 1000,
    autoplay: true,
    autoplaySpeed: 15000,
    slidesToShow: 1,
    slidesToScroll: 1,
    initialSlide: 0,
    cssEase: "ease",
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    beforeChange: (current, next) => setActiveSlide(next),
    afterChange: (current) => setActiveSlide2(current),
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          initialSlide: 1
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  };

  const slides = [
    {
      title: "WELCOME TO DevVerse",
      description: "Leading top software house in Pakistan. We offer our services in web designing, CRM systems, digital marketing, and mobile apps.",
      buttonText: "Read More",
      imageSrc: bg_slide1,
      backgroundColor: "",
    },
    {
      title: "Mobile Apps Development",
      description: "We know how to build Apps that will help you increase business.",
      buttonText: "Read More",
      imageSrc: "",
      backgroundColor: "#C0C0C0",
    },
    {
      title: "Web Development Responsive & Reliable",
      description: "We plan, design your website from start to finish, ensuring a high ROI for clients. Our major concern is client satisfaction.",
      buttonText: "Read More",
      imageSrc: "",
      backgroundColor: "#a9b0b4",
    },
    {
      title: "Search Engine Optimization",
      description: "We start the SEO process with a full website audit and recommendations report. Our team will review your key metrics, perform keyword research, analyze your competitors, your linkscape, and key pages of your website, and create a detailed implementation plan.",
      buttonText: "Read More",
      imageSrc: "",
      backgroundColor: "#f5f5f5",
    },
  ];

  return (
    <div
    className="custom-carousel-wrapper"
    onMouseEnter={handleMouseEnter}
    onMouseLeave={handleMouseLeave}
  >
    <Slider {...settings} ref={sliderRef}>
      {slides.map((slide, index) => (
        <div key={index}>
          <div
            className="slide-content"
            style={{
              height: "400px",
              display: "flex",
              background: `url(${slide.imageSrc}) center/cover no-repeat`,
              backgroundColor: slide.backgroundColor,
            }}
          >
            <AnimatePresence exitBeforeEnter={false} initial={false}>
              {activeSlide === index && (
                <SlideContent
                  title={slide.title}
                  description={slide.description}
                  buttonText={slide.buttonText}
                  imageSrc={slide.imageSrc}
                  index={index}
                />
              )}
            </AnimatePresence>
          </div>
        </div>
      ))}
    </Slider>
    {areArrowsVisible && (
      <div className="arrows-container">
        <PrevArrow />
        <NextArrow />
      </div>
    )}
  </div>
  );
};

export default Carousel;
