import React, { useEffect } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import AOS from 'aos';
import 'aos/dist/aos.css';

const Carousel = () => {
  // Initialize AOS with custom settings
  useEffect(() => {
    AOS.init({
      duration: 1000, // Adjust as needed
      delay: 100,     // Adjust as needed
    });

    setTimeout(() => {
      AOS.refresh();
    }, 100);
  }, []);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  return (
    <Slider {...settings}>
      <div data-aos="fade-up" style={{ height: "200px", font:"black" }}>
        <h1>Slide 1: hey this is my photo</h1>
        <h2>Subtitle 1: sdsaefae</h2>
      </div>
      <div data-aos="fade-up">
        <h1>Slide 2: Your content here</h1>
        <p>Additional content for the second slide</p>
      </div>
      {/* Add more slides as needed */}
    </Slider>
  );
};

export default Carousel;
