import React, { useEffect } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import AOS from 'aos';
import 'aos/dist/aos.css';

const Carousel = () => {
  useEffect(() => {
    AOS.init();
  }, []);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    afterChange: () => {
      // Manually trigger AOS animation for all elements in the slider
      const elements = document.querySelectorAll(`[data-aos='fade-up']`);
      if (elements.length > 0) {
        AOS.refresh();
        AOS.refreshHard(); // Refresh AOS with hard refresh to force animations
        elements.forEach(element => {
          AOS.refreshElement(element);
          AOS.trigger(element, 'aos:in', {});
        });
      }
    },
  };

  return (
    <Slider {...settings}>
      <div data-aos="fade-up" style={{ height: "200px" }}>
        <h1>Slide 1: hey this is my photo</h1>
        <h2>Subtitle 1: sdsaefae</h2>
      </div>
      <div data-aos="fade-up">
        <h1>Slide 2: Your content here</h1>
        <p>Additional content for the second slide</p>
      </div>
      {/* Add more slides as needed */}
    </Slider>
  );
};

export default Carousel;
