import React, { useState } from 'react';


const FAQSection = () => {
  const [openIndex, setOpenIndex] = useState(null);

  const handleAccordionToggle = (index) => {
    console.log('Clicked on index:', index);
    setOpenIndex((prevIndex) => (prevIndex === index ? null : index));
  };

  const faqData = [
    {
      question: 'How much will it cost?',
      answer:
        'To get a better idea of the cost of what you want to build, give us a quick phone call...',
    },
    {
      question: 'How long it takes to design?',
      answer:
        'The time limit of any assignment is normally dictated by the client. If you have any time limit in mind...',
    },
    {
      question: 'How do I associate with IT Company?',
      answer:
        'The process begins when you contact us with your requirements. We analyze your requirements and respond to you...',
    },
    {
      question: 'Can you help my current site look more professional?',
      answer:
        'Yes. Give us your requirements and we have experienced expertise to help you give a new professional look that really wonders!',
    },
    {
      question: 'When do I pay?',
      answer:
        'For most projects, equal payments are made at the start, midway, and the end of the project...',
    },
  ];

  return (
    <section className="faq">
      <h1 className="heading" style={{ marginTop: '-2rem', color: '#00bfff' }} id="faq">
        FAQ
      </h1>
      <div className="row">
        <div className="accordion-container">
          {faqData.map((faq, index) => (
            <div className="accordion" key={index}>
              <div
                className={`accordion-header ${openIndex === index ? 'open' : ''}`}
                onClick={() => handleAccordionToggle(index)}
              >
                <span>{openIndex === index ? '-' : '+'}</span>
                <h3>{faq.question}</h3>
              </div>
              <div className={`accordion-body ${openIndex === index ? 'open' : 'closed'}`}>
                {openIndex === index && (
                  <p>
                    {faq.answer}
                    <br /> {/* Adding a line break for clarity */}
                    This paragraph is shown for index {index}.
                  </p>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default FAQSection;
