import React, { useEffect } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import AOS from 'aos';
import 'aos/dist/aos.css';

const Carousel = () => {
  useEffect(() => {
    AOS.init();
  }, []);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    afterChange: (currentSlide) => {
      // Manually trigger AOS animation when the slide changes
      const elements = document.querySelectorAll(`[data-aos='fade-up'][data-aos-anchor='${currentSlide}']`);
      if (elements.length > 0) {
        AOS.refresh();
        AOS.refreshHard(); // Refresh AOS with hard refresh to force animations
        elements.forEach(element => {
          element.setAttribute('data-aos', 'fade-up');
          AOS.refreshElement(element);
          AOS.trigger(element, 'aos:in', {});
        });
      }
    },
  };

  return (
    <Slider {...settings}>
      <div data-aos-anchor="0" style={{ height: "200px" }}>
        <h1 data-aos="fade-up">Slide 1: hey this is my photo</h1>
        <h2 data-aos="fade-up">Subtitle 1: sdsaefae</h2>
      </div>
      <div data-aos-anchor="1">
        <h1 data-aos="fade-up">Slide 2: Your content here</h1>
        <p data-aos="fade-up">Additional content for the second slide</p>
      </div>
      {/* Add more slides as needed */}
    </Slider>
  );
};

export default Carousel;
