import React, { useEffect, useRef } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { motion, useAnimation } from 'framer-motion';
import bg_slide1 from "../../images/img1.jpg";

import './carousel.css'; // Import your custom styles

const SlideContent = ({ title, description, buttonText }) => {
  const titleControls = useAnimation();
  const descriptionControls = useAnimation();
  const buttonControls = useAnimation();

  useEffect(() => {
    // Initial animations when the component mounts
    titleControls.start({ opacity: 1, y: 0 });
    descriptionControls.start({ opacity: 1, y: 0 });
    buttonControls.start({ opacity: 1, scale: 1 });
  }, [titleControls, descriptionControls, buttonControls]);

  return (
    <div>
      <motion.h1 initial={{ opacity: 0, y: 50 }} animate={titleControls}>
        {title}
      </motion.h1>
      <motion.p initial={{ opacity: 0, y: 50 }} animate={descriptionControls}>
        {description}
      </motion.p>
      <motion.button
        initial={{ opacity: 0, scale: 0.5 }}
        animate={buttonControls}
      >
        {buttonText}
      </motion.button>
    </div>
  );
};

const Carousel = () => {
  const sliderRef = useRef(null);

  useEffect(() => {
    // Explicitly start the autoplay when the component mounts
    if (sliderRef.current) {
      sliderRef.current.slickPlay();
    }
  }, []);

  const settings = {
    dots: true,
    infinite: true,
    speed: 1000,
    autoplay: true,
    autoplaySpeed: 2000, // Adjust the autoplay speed as needed
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  const slides = [
    {
      title: "WELCOME TO DevVerse",
      description: "Leading top software house in Pakistan. We offer our services in web designing, CRM systems, digital marketing, and mobile apps.",
      buttonText: "Read More",
      imageSrc: bg_slide1,
      backgroundColor: "",
    },
    {
      title: "Mobile Apps Development",
      description: "We know how to build Apps that will help you increase business.",
      buttonText: "Read More",
      imageSrc: "",
      backgroundColor: "#7d99d4",
    },
    {
      title: "Web Development Responsive & Reliable",
      description: "We plan, design your website from start to finish, ensuring a high ROI for clients. Our major concern is client satisfaction.",
      buttonText: "Read More",
      imageSrc: "",
      backgroundColor: "#ffcccb",
    },
    {
      title: "Search Engine Optimization",
      description: "We start the SEO process with a full website audit and recommendations report. Our team will review your key metrics, perform keyword research, analyze your competitors, your linkscape, and key pages of your website, and create a detailed implementation plan.",
      buttonText: "Read More",
      imageSrc: "",
      backgroundColor: "#aee4e1",
    },
  ];

  return (
    <div className="custom-carousel-wrapper">
      <Slider {...settings} ref={sliderRef}>
        {slides.map((slide, index) => (
          <div key={index}>
            <div
              style={{
                height: "400px",
                display: "flex",
                background: `url(${slide.imageSrc}) center/cover no-repeat`,
                backgroundColor: slide.backgroundColor,
              }}
            >
              <SlideContent
                title={slide.title}
                description={slide.description}
                buttonText={slide.buttonText}
              />
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default Carousel;
