import React, { useRef, useState } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { motion, AnimatePresence } from 'framer-motion';
import bg_slide1 from "../../images/img1.jpg";

import './carousel.css';

function PrevArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{
        ...style,
        display: "block",
        padding: "10px",
        borderRadius: "50%",
        cursor: "pointer",
        left: "0px",
        zIndex: 1,
      }}
      onClick={onClick}
    ></div>
  );
}

function NextArrow(props) {
  const { className, style, onClick } = props;

  return (
    <div
      className={className}
      style={{
        ...style,
        display: "block",
        padding: "50px",
        borderRadius: "100%",
        cursor: "pointer",
      }}
      onClick={onClick}
    ></div>
  );
}
const SlideContent = ({ title, description, buttonText, rightImageSrc, index }) => {
  const isFirstSlide = index === 0;

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.5 }}
      animate={{ opacity: 1, scale: 1 }}
      exit={{ opacity: 0, scale: 0.5 }}
      transition={{ duration: 0.8, ease: 'easeOut' }}
      className="content"
      style={{
        backgroundColor: isFirstSlide ? 'transparent' : '#f5f5f5', // Use transparent for the first slide
        backgroundImage: isFirstSlide ? `url(${rightImageSrc})` : 'none', // Use background image for the first slide
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
      }}
    >
      <div className="slide-content">
        <div className="text-content">
          <motion.h1 initial={{ rotateY: 180 }} animate={{ rotateY: 0 }} transition={{ duration: 1, ease: 'easeOut' }}>
            {title}
          </motion.h1>
          <motion.p initial={{ x: -50, opacity: 0 }} animate={{ x: 0, opacity: 1 }} transition={{ duration: 0.8, ease: 'easeOut', delay: 1.5 }}>
            {description}
          </motion.p>
          <motion.button initial={{ scale: 0 }} animate={{ scale: 1 }} transition={{ duration: 0.8, ease: 'easeOut', delay: 2.5 }}>
            {buttonText}
          </motion.button>
        </div>
        {isFirstSlide && (
          <div className="image-content">
            <img src={rightImageSrc} alt={title} />
          </div>
        )}
      </div>
    </motion.div>
  );
};



const Carousel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [areArrowsVisible, setAreArrowsVisible] = useState(false);

  const handleMouseEnter = () => {
    setAreArrowsVisible(true);
  };

  const handleMouseLeave = () => {
    setAreArrowsVisible(false);
  };

  const sliderRef = useRef(null);
  const slides = [
    {
      title: "WELCOME TO DevVerse",
      description: "Leading top software house in Pakistan. We offer our services in web designing, CRM systems, digital marketing, and mobile apps.",
      buttonText: "Read More",
      rightImageSrc: "https://example.com/right-image1.jpg", // Add the right image source
      backgroundColor: "",
    },
    {
      title: "Mobile Apps Development",
      description: "We know how to build Apps that will help you increase business.",
      buttonText: "Read More",
      rightImageSrc: "https://example.com/right-image2.jpg", // Add the right image source
      backgroundColor: "#C0C0C0",
    },
    {
      title: "Web Development Responsive & Reliable",
      description: "We plan, design your website from start to finish, ensuring a high ROI for clients. Our major concern is client satisfaction.",
      buttonText: "Read More",
      rightImageSrc: "https://example.com/right-image3.jpg", // Add the right image source
      backgroundColor: "#a9b0b4",
    },
    {
      title: "Search Engine Optimization",
      description: "We start the SEO process with a full website audit and recommendations report. Our team will review your key metrics, perform keyword research, analyze your competitors, your linkscape, and key pages of your website, and create a detailed implementation plan.",
      buttonText: "Read More",
      rightImageSrc: "https://example.com/right-image4.jpg", // Add the right image source
      backgroundColor: "#f5f5f5",
    },
  ];

  const settings = {
    dots: true,
    infinite: false,
    focusOnSelect: true,
    speed: 1000,
    autoplay: true,
    autoplaySpeed: 15000,
    slidesToShow: 1,
    slidesToScroll: 1,
    initialSlide: 0,
    cssEase: "ease",
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    beforeChange: (current, next) => setCurrentSlide(next),
    afterChange: (current) => setCurrentSlide(current),
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          initialSlide: 1
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  };

  return (
    <div
      className="custom-carousel-wrapper"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <Slider {...settings} ref={sliderRef}>
        {slides.map((slide, index) => (
          <div key={index}>
            <div
              className="slide-content"
              style={{
                height: "400px",
                display: "flex",
                background: `url(${slide.rightImageSrc}) center/cover no-repeat`,
                backgroundColor: slide.backgroundColor,
              }}
            >
              <AnimatePresence exitBeforeEnter={false} initial={false}>
                {currentSlide === index && (
                  <SlideContent
                    title={slide.title}
                    description={slide.description}
                    buttonText={slide.buttonText}
                    rightImageSrc={slide.rightImageSrc}
                  />
                )}
              </AnimatePresence>
            </div>
          </div>
        ))}
      </Slider>
      {areArrowsVisible && (
        <div className="arrows-container">
          <PrevArrow />
          <NextArrow />
        </div>
      )}
    </div>
  );
};


export default Carousel;
