import React, { useRef, useState } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { motion, AnimatePresence } from 'framer-motion';
import bg_slide1 from "../../images/img2.jpg";

import './carousel.css';

function PrevArrow(props) {
  const { className, style, onClick } = props;
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      className={className}
      style={{
        ...style,
        display: "block",
        padding: "50px",
        borderRadius: "10px",
        width: "50px",
        height: "50px",
        cursor: "pointer",
        left: "0rem",
        zIndex: 1,
        background: `rgba(255, 255, 255, ${isHovered ? 0.7 : 0.3})`,
        position: "absolute",
      }}
      onClick={onClick}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <i className="fas fa-chevron-left"></i>
    </div>
  );
}

function NextArrow(props) {
  const { className, style, onClick } = props;
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      className={className}
      style={{
        ...style,
        display: "block",
        padding: "50px",
        width: "50px",
        height: "50px",
        borderRadius: "10px",
        cursor: "pointer",
        right: "0px",
        background: `rgba(255, 255, 255, ${isHovered ? 0.7 : 0.3})`,
      }}
      onClick={onClick}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <i className="fas fa-chevron-right"></i>
    </div>
  );
}

const SlideContent = ({ title1,title2, description, buttonText, rightImageSrc,isButtonVisible ,index}) => {
  const [isTapped, setIsTapped] = useState(false);
  const handleTapStart = () => {
    setIsTapped(true);
  };

  const handleTapEnd = () => {
    setIsTapped(false);
    // Implement the functionality for the 'Read More' action here
  };

  const handleTapCancel = () => {
    setIsTapped(false);
  };
  const isFirstSlide = index === 0;
  return (

    <motion.div
    initial={{ opacity: 0, scale: 0.5 }}
    animate={{ opacity: 1, scale: 1 }}
    exit={{ opacity: 0, scale: 0.5 }}
    transition={{ duration: 0.8, ease: 'easeOut' }}
    className="content"
  >
    <div className="slide-content">
      <div className="text-content">
        <motion.h1
          style={{
            color: "red",
            fontSize:"43px", // Set your desired font size
          }}
          initial={{ translateY: "100%", opacity: 0 }}
          animate={{ translateY: "0%", opacity: 1 }}
          transition={{ duration: 1, ease: 'easeOut', delay: 0.5 }}
        >
          {title1}
        </motion.h1>
        <motion.h1
          style={{
            color: isFirstSlide ? "red" : "black",
            fontSize: isFirstSlide ? "45px" : "43px", // Set your desired font size
          }}
          initial={{ translateY: "100%", opacity: 0 }}
          animate={{ translateY: "0%", opacity: 1 }}
          transition={{ duration: 1, ease: 'easeOut', delay: 1.0 }}
        >
          {title2}
        </motion.h1>
        <motion.p
          style={{
            color: isFirstSlide ? "white" : "black",
            fontSize: isFirstSlide ? "20px" : "15px", // Set your desired font size
          }}
          initial={{ x: -50, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ duration: 0.8, ease: 'easeOut', delay: 1.5 }}
        >
          {description}
        </motion.p>
        {isButtonVisible && (
          <motion.button
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ duration: 0.5, ease: 'easeOut', delay: 2.5 }}
            whileTap={{ scale: 0.8 }}
            onTapStart={handleTapStart}
            onTap={handleTapEnd}
            onTapCancel={handleTapCancel}
            className={`your-read-more-button-styles ${isTapped ? 'tapped' : ''}`}
            style={{
              fontSize: "1.2em",
              marginTop: "20px",
            }}
          >
            {buttonText}
          </motion.button>
        )}
      </div>
      {rightImageSrc && (
        <motion.div
          className="image-content"
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, ease: 'easeOut', delay: 1.0 }}
        >
          <motion.img
            src={rightImageSrc}
            alt={title1}
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8, ease: 'easeOut', delay: 1.0 }}
            style={{
              width: "100%",
              borderRadius: "10px",
            }}
          />
        </motion.div>
      )}
    </div>
  </motion.div>

  );
};


const Carousel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [areArrowsVisible, setAreArrowsVisible] = useState(false);

  const handleMouseEnter = () => {
    setAreArrowsVisible(true);
  };

  const handleMouseLeave = () => {
    setAreArrowsVisible(false);
  };

  const sliderRef = useRef(null);
  const slides = [
    {
      title1: "WELCOME TO",
      title2: "DevVerse",
      description: "Leading top software house in Pakistan. We offer our services in web designing, CRM systems, digital marketing, and mobile apps.",
      rightImageSrc: bg_slide1, // Add the right image source

    },
    {
      title1: "Mobile Apps Development",
      title2: "We Build Awesome Apps",
      description: "We know how to build Apps that will help you increase business.",
      buttonText: "Read More",
      rightImageSrc: "https://cdn.pixabay.com/photo/2023/02/26/07/37/android-mobile-app-development-7815022_1280.png", // Add the right image source
      backgroundColor:"#f2f3f4"
    },
    {

      title1: "Web Development",
      title2: "Responsive & Reliable",
      description: "We plan, design your website from start to finish, ensuring a high ROI for clients. Our major concern is client satisfaction.",
      buttonText: "Read More",
      rightImageSrc: "https://cdn.pixabay.com/photo/2019/10/09/07/28/development-4536630_1280.png", // Add the right image source
      backgroundColor:"#e5e4e2 "
    },
    {
      title1: "Search Engine",
      title2: "Optimization",
      description: "We start the SEO process with a full website audit and recommendations report. Our team will review your key metrics, perform keyword research, analyze your competitors, your linkscape, and key pages of your website, and create a detailed implementation plan.",
      buttonText: "Read More",
      rightImageSrc: "https://cdn.pixabay.com/photo/2016/10/17/03/01/seo-1746842_1280.png", // Add the right image source
      backgroundColor:"#bebebe  "
    },
  ];

  const settings = {
    dots: true,
    infinite: false,
    focusOnSelect: true,
    speed: 1000,
    autoplay: true,
    autoplaySpeed: 15000,
    slidesToShow: 1,
    slidesToScroll: 1,
    initialSlide: 0,
    cssEase: "ease",
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    beforeChange: (current, next) => setCurrentSlide(next),
    afterChange: (current) => setCurrentSlide(current),
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          initialSlide: 1
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  };

  return (
    <div
    className="custom-carousel-wrapper"
    onMouseEnter={handleMouseEnter}
    onMouseLeave={handleMouseLeave}
  >
    <Slider {...settings} ref={sliderRef}>
      {slides.map((slide, index) => (
        <div key={index}>
          <div
            className="slide-content"
            style={{
              height: "550px",
              display: "flex",
              background:
                index === 0
                  ? `url(${slide.rightImageSrc}) center/cover no-repeat`
                  : slide.backgroundColor,
            }}
          >
            <AnimatePresence exitBeforeEnter={false} initial={false}>
              {currentSlide === index && (
                <SlideContent
                  title1={slide.title1}
                  title2={slide.title2}
                  description={slide.description}
                  buttonText={slide.buttonText}
                  rightImageSrc={slide.rightImageSrc}
                  isButtonVisible={index !== 0}
                />
              )}
            </AnimatePresence>
          </div>
        </div>
      ))}
    </Slider>
    {areArrowsVisible && (
      <div className="arrows-container">
        <PrevArrow />
        <NextArrow />
      </div>
    )}
  </div>
  );
};


export default Carousel;
