import React, { useEffect } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import AOS from 'aos';
import 'aos/dist/aos.css';

const Carousel = () => {
  useEffect(() => {
    AOS.init();
  }, []);

  const slideStyle = {
    height: "400px", // Set the fixed height for all slides
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    textAlign: "center",
    background: "#eee", // Optional: Set a background color for the slides
  };

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    beforeChange: (current, next) => {
      // Trigger AOS refresh before each slide change
      AOS.refresh();
    },
  };

  return (
    <Slider {...settings}>
      <div data-aos="fade-up" style={slideStyle}>
        <div>
          <h1>Slide 1: hey this is my photo</h1>
          <h2>Subtitle 1: sdsaefae</h2>
        </div>
      </div>
      <div data-aos="fade-up" style={slideStyle}>
        <div>
          <h1>Slide 2: Your content here</h1>
          <p>Additional content for the second slide</p>
        </div>
      </div>
      {/* Add more slides as needed */}
    </Slider>
  );
};

export default Carousel;
