import React, { useEffect, useState } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import AOS from 'aos';
import 'aos/dist/aos.css';

const Carousel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    AOS.init();
  }, []);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    beforeChange: (current, next) => {
      setCurrentSlide(next);
    },
  };

  const handleSlideChange = () => {
    // Manually trigger AOS refresh after each slide change
    AOS.refresh();
    // Reset the animation state for all elements
    document.querySelectorAll(`[data-aos]`).forEach(element => {
      element.removeAttribute('data-aos-anchor');
    });
    // Trigger animation for the next slide
    document.querySelectorAll(`[data-aos]`).forEach(element => {
      element.setAttribute('data-aos-anchor', `${currentSlide}`);
      AOS.refreshElement(element);
      AOS.trigger(element, 'aos:in', {});
    });
  };

  return (
    <Slider {...settings} beforeChange={handleSlideChange}>
      <div data-aos="fade-up" style={{ height: "200px" }}>
        <h1>Slide 1: hey this is my photo</h1>
        <h2>Subtitle 1: sdsaefae</h2>
      </div>
      <div data-aos="fade-up">
        <h1>Slide 2: Your content here</h1>
        <p>Additional content for the second slide</p>
      </div>
      {/* Add more slides as needed */}
    </Slider>
  );
};

export default Carousel;
