import React, { useEffect } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import AOS from 'aos';
import 'aos/dist/aos.css';
import bg_slide1 from "../../images/img1.jpg";

import './carousel.css'; // Import your custom styles

const Carousel = () => {
  const sliderRef = useRef(null);
  useEffect(() => {
    AOS.init();
  }, []);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    beforeChange: (current, next) => {
      // Trigger AOS refresh before each slide change
      refreshAnimations();
    },
  };
  const refreshAnimations = () => {
    requestAnimationFrame(() => {
      const carouselElements = sliderRef.current.querySelectorAll(`[data-aos]`);
      carouselElements.forEach(element => {
        AOS.refresh(element);
        // Retrigger the animation for the current slide:
        AOS.animate(element, 'fade-up');
      });
    });
  };

  const slides = [
    {
      title: "WELCOME TO DevVerse",
      description: "Leading top software house in Pakistan. We offer our services in web designing, CRM systems, digital marketing, and mobile apps.",
      buttonText: "Read More",
      imageSrc: bg_slide1,
      backgroundColor: "",
    },
    {
      title: "Mobile Apps Development",
      description: "We know how to build Apps that will help you increase business.",
      buttonText: "Read More",
      imageSrc: "https://placekitten.com/300/201",
      backgroundColor: "#7d99d4",
    },
    {
      title: "Web Development Responsive & Reliable",
      description: "We plan, design your website from start to finish, ensuring a high ROI for clients. Our major concern is client satisfaction.",
      buttonText: "Read More",
      imageSrc: "https://placekitten.com/300/201",
      backgroundColor: "#ffcccb",
    },
    {
      title: "Search Engine Optimization",
      description: "We start the SEO process with a full website audit and recommendations report. Our team will review your key metrics, perform keyword research, analyze your competitors, your linkscape, and key pages of your website, and create a detailed implementation plan.",
      buttonText: "Read More",
      imageSrc: "https://placekitten.com/300/201",
      backgroundColor: "#aee4e1",
    },
  ];

  return (
    <div className="custom-carousel-wrapper">
      <Slider {...settings}>
        {slides.map((slide, index) => (
          <div
            key={index}
            data-aos="fade-up"
            style={{
              height: "400px",
              display: "flex",
              background: `url(${slide.imageSrc}) center/cover no-repeat`,
              backgroundColor: slide.backgroundColor,
            }}
          >
            <div style={{ flex: 1, padding: "20px", backgroundColor: "rgba(255, 255, 255, 0.8)" }}>
              <h1>{slide.title}</h1>
              <p>{slide.description}</p>
              <button>{slide.buttonText}</button>
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default Carousel;
