import React, { useEffect, useRef, useState } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import AOS from 'aos';
import 'aos/dist/aos.css';

const Carousel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const sliderRef = useRef(null);

  useEffect(() => {
    AOS.init();
  }, []);

  useEffect(() => {
    AOS.refresh();
    requestAnimationFrame(() => {
      const carouselElements = sliderRef.current.querySelectorAll(`[data-aos]`);
      carouselElements.forEach(element => {
        element.removeAttribute('data-aos-anchor');
        element.setAttribute('data-aos-anchor', `${currentSlide}`);
        AOS.refreshElement(element);
        AOS.trigger(element, 'aos:in', {});
      });
    });
  }, [currentSlide]);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    beforeChange: (current, next) => {
      setCurrentSlide(next);
    },
  };

  return (
    <Slider {...settings} ref={sliderRef} beforeChange={handleSlideChange}>
      {currentSlide === 0 && (
        <div data-aos="fade-up" style={{ height: "200px" }}>
          <h1>Slide 1: hey this is my photo</h1>
          <h2>Subtitle 1: sdsaefae</h2>
        </div>
      )}
      {currentSlide === 1 && (
        <div data-aos="fade-up">
          <h1>Slide 2: Your content here</h1>
          <p>Additional content for the second slide</p>
        </div>
      )}
      {/* Add more slides as needed, conditionally rendering them based on currentSlide */}
    </Slider>
  );
};

export default Carousel;
