const HiddenModals = ({ projects }) => {
    const [showModal, setShowModal] = useState(null);

    function showModalHandler(name) {
        setShowModal(name);
    }

    function hideModal() {
        const modalBG = document.getElementById("gallery-card");
        modalBG.style.display = "none";
        setShowModal(null);
    }

    const style = {
        textAlign: "right",
        fontWeight: 900,
        fontSize: "2rem",
        cursor: "pointer"
    };

    return (
        <div id="gallery-card">
            {projects.map((project, index) => (
                <div
                    id={project.name}
                    className={`modal-card ${showModal === project.name ? 'show' : ''}`}
                    key={index}
                >
                    <div className="visual">
                        <img src={project.image} alt="" />
                    </div>
                    <div className="modal-info">
                        <h2>{project.name}</h2>
                        <HiddenModalDescription description={project.description} />
                        <div className="modal-bottom">
                            <a href={project.ref} target="_blank" rel="noopener noreferrer">
                                <h3>{project.status}</h3>
                            </a>
                            <p className="close-icon" style={style} onClick={hideModal}>&#10005;</p>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default HiddenModals;
