import React, { useEffect } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import AOS from 'aos';
import 'aos/dist/aos.css';
import "../../images/img1.jpg"
const Carousel = () => {
  useEffect(() => {
    AOS.init();
  }, []);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    beforeChange: (current, next) => {
      // Trigger AOS refresh before each slide change
      AOS.refresh();
    },
  };

  const slides = [
    {
      title: "Slide 1",
      description: "Description for Slide 1. Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
      buttonText: "Learn More",
      imageSrc: "", // Example image URL
      backgroundColor: "", // Example background color
    },
    {
      title: "Slide 2",
      description: "Description for Slide 2. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
      buttonText: "Explore",
      imageSrc: "https://placekitten.com/300/201", // Example image URL
      backgroundColor: "#ffcccb", // Example background color
    },
    // Add more slides as needed
  ];

  return (
    <Slider {...settings}>
      {slides.map((slide, index) => (
        <div
          key={index}
          data-aos="fade-up"
          style={{
            height: "400px",
            display: "flex",
            backgroundColor: slide.backgroundColor,
          }}
        >
          <div style={{ flex: 1, padding: "20px" }}>
            <h1>{slide.title}</h1>
            <p>{slide.description}</p>
            <button>{slide.buttonText}</button>
          </div>
          <div style={{ flex: 1 }}>
            <img src={slide.imageSrc} alt={`Slide ${index + 1}`} style={{ width: "100%", height: "100%", objectFit: "cover" }} />
          </div>
        </div>
      ))}
    </Slider>
  );
};

export default Carousel;
